const itemInCart = (cart, item) => cart.filter(cartItem => cartItem.id === item.id)[0]

const itemOnFocus = (currentItem, item) => {
	console.log(item)
	console.log(currentItem)
	return item === undefined
		? [{id: 1, name: "Business Insurance: Budget", description: "Product 1 description xxxxxxxxxxxx", price: 20}]
		: [item]
	return [item]
}

const detailsReducer = (state=[], action) => {
	switch(action.type){
		case 'FOCUS':
			return itemOnFocus(state, action.payload)

		default:
			return state
	}
}

export default detailsReducer
