const searchProducts = (products, searchParam) => {
	console.log(searchParam)
	console.log(products)
	// return products === undefined ? products : products.filter(item => item.name.match(searchParam))
	return products = ['1']
}

const searchReducer = (state=[], action) => {
	switch(action.type){
		case 'SEARCH':
			return searchProducts(state, action.payload)

		default:
			return state
	}
}

export default searchReducer
