const searchProducts = (searchParam) => {
	return searchParam
}

const searchReducer = (state=[], action) => {
	switch(action.type){
		case 'SEARCH':
			console.log(action.payload)
			return searchProducts(state, action.payload)

		default:
			return state
	}
}

export default searchReducer
