const itemOnFocus = (searchParam) => {
	return [searchParam]
}

const searchReducer = (state=[], action) => {
	switch(action.type){
		case 'SEARCH':
			return itemOnFocus(action.payload)

		default:
			return state
	}
}

export default searchReducer
