import React from 'react'
import ProductListItem from './product-list-item'
import { connect } from 'react-redux'
import { cartItemsWithQuantities } from '../cart';

import { Dropdown} from 'semantic-ui-react'

function ProductListing(props) {
	let filteredList = props.search === undefined ? props.products : props.products.filter(item => item.name.toLowerCase().includes(props.search.toLowerCase()))
	return <div className='product-listing'>
		<form className='search-form'>
			<input onChange={props.searchProducts} value={props.search} placeholder='Search Products' className='search-form-input' />
		</form>
		<Dropdown text='File'>
		    <Dropdown.Menu>
		      <Dropdown.Item text='New' />
		      <Dropdown.Item text='Open...' description='ctrl + o' />
		      <Dropdown.Item text='Save as...' description='ctrl + s' />
		      <Dropdown.Item text='Rename' description='ctrl + r' />
		      <Dropdown.Item text='Make a copy' />
		      <Dropdown.Item icon='folder' text='Move to folder' />
		      <Dropdown.Item icon='trash' text='Move to trash' />
		      <Dropdown.Divider />
		      <Dropdown.Item text='Download As...' />
		      <Dropdown.Item text='Publish To Web' />
		      <Dropdown.Item text='E-mail Collaborators' />
		    </Dropdown.Menu>
		  </Dropdown>
	{
		filteredList.map( product =>
			<ProductListItem  
			key={product.id}
			product={product}
			itemOnFocus={props.itemOnFocus}
			addToCart={props.addToCart}
			removeFromCart={props.removeFromCart}
			cartItem={props.cart.filter(cartItem => cartItem.id === product.id)[0]}
			/>)
	}
	</div>
}

function mapStateToProps(state) {
	return {
		cart: state.cart,
		details: state.details,
		search: state.search
	}
}

function mapDispatchToProps(dispatch) {
	return {
		searchProducts: (searchEvent) => {
			dispatch({ type: 'SEARCH', payload: searchEvent.target.value })
		},
		itemOnFocus: (item) => {
			dispatch({ type: 'FOCUS', payload: item })
		},
		addToCart: (item) => {
			dispatch({ type: 'ADD', payload: item })
		},
		removeFromCart: (item) => {
			dispatch({ type: 'REMOVE', payload: item })
		}
	}
}

export default connect(mapStateToProps, mapDispatchToProps)(ProductListing)
